import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import MedicineReminder from './MedicineReminder';
import './Dashboard.css';

const Dashboard = () => {
  const [medicines, setMedicines] = useState([]);

  // Check if the user is logged in
  useEffect(() => {
    const user = localStorage.getItem('user');
    if (!user) {
      history.push('/login'); // Redirect to Login if not logged in
    }
  }, [history]);

  return (
    <div className="dashboard-container">
      <h1>Welcome to Your Medicine Reminder Dashboard</h1>
      <p>Here you can manage your medicine reminders.</p>

      {/* MedicineReminder component */}
      <MedicineReminder />

      <button onClick={() => {
        localStorage.removeItem('user'); // Log out user
        history.push('/login'); // Redirect to login page
      }}>
        Log Out
      </button>
    </div>
  );
};

export default Dashboard;
