import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (!user) {
      navigate('/login');
    }
  }, [navigate]);

  return (
    <div className="dashboard-wrapper">
      <div className="left-panel">
        <img src="/med.jpg" alt="Medicine Illustration" className="illustration" />
        <h2>We take care of your regular medication</h2>
        <p>Keep yourself and your loved ones safe and never forget to take your meds, supplements, and vitamins.</p>
        <button className="get-started" onClick={() => navigate('/schedule')}>Get Started</button>
      </div>

      <div className="right-panel">
        <div className="header-top">
          <span className="user-greeting">Hello 👋</span>
          <div className="auth-buttons">
            <button onClick={() => navigate('/login')}>Login</button>
            <button onClick={() => navigate('/register')}>Register</button>
          </div>
        </div>

        <div className="schedule-section">
          <h3>CREATE NEW SCHEDULE</h3>
          <button className="start-btn" onClick={() => navigate('/schedule')}>Start</button>
          <div className="calendar-row">
            {['2', '3', '4', '5', '6', '7'].map((day, idx) => (
              <span key={idx} className={`day ${day === '5' ? 'selected' : ''}`}>{day}</span>
            ))}
          </div>

          <div className="medicine-list">
            <div className="medicine-item">
              <img src="/vitamin.png" alt="Vitamin C" />
              <div>
                <strong>Vitamin C</strong>
                <p>2 Capsules - 8:30 AM</p>
              </div>
            </div>
            <div className="medicine-item">
              <img src="/pill2.png" alt="Valtium" />
              <div>
                <strong>Valtium plus 25</strong>
                <p>2 Pills - 8:00 AM</p>
              </div>
            </div>
            <div className="medicine-item highlight">
              <img src="/centerpill.png" alt="Centrum" />
              <div>
                <strong>Centrum</strong>
                <p>1 Capsule - 10:30 PM</p>
              </div>
            </div>
            {/* Add more items if needed */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
