import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [medicines, setMedicines] = useState([]);
  const [form, setForm] = useState({ name: '', dosage: '', time: '', email: '' });

  // Load from localStorage on mount
  useEffect(() => {
    const storedMedicines = JSON.parse(localStorage.getItem('medicines')) || [];
    setMedicines(storedMedicines);
  }, []);

  // Save to localStorage when medicines change
  useEffect(() => {
    localStorage.setItem('medicines', JSON.stringify(medicines));
  }, [medicines]);

  // Request Notification Permission
  useEffect(() => {
    if (Notification.permission !== 'granted') {
      Notification.requestPermission();
    }
  }, []);

  // Check for medicine time every minute
  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      const currentTime = now.toTimeString().slice(0, 5); // Format: HH:MM
      medicines.forEach((med) => {
        if (med.time === currentTime) {
          notifyUser(med);
        }
      });
    }, 60000); // Every minute

    return () => clearInterval(interval); // Cleanup
  }, [medicines]);

  const notifyUser = (med) => {
    if (Notification.permission === 'granted') {
      new Notification('Medicine Reminder', {
        body: `${med.name} - ${med.dosage} now!`,
        icon: 'https://cdn-icons-png.flaticon.com/512/2920/2920244.png',
      });
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedMedicines = [...medicines, form];
    setMedicines(updatedMedicines);
    setForm({ name: '', dosage: '', time: '', email: '' });
  };

  return (
    <div className="app">
      <h1>Medicine Reminder App</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Medicine Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="dosage"
          placeholder="Dosage"
          value={form.dosage}
          onChange={handleChange}
          required
        />
        <input
          type="time"
          name="time"
          value={form.time}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Your Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <button type="submit">Add Reminder</button>
      </form>

      <h2>Scheduled Medicines</h2>
      <ul>
        {medicines.map((med, index) => (
          <li key={index}>
            {med.name} - {med.dosage} at {med.time}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
